// Whole number
const seven = 7; // Integer.

// Decimal number
const PI = 3.14; // Floating point; double precision number.

console.log(
    'seven:',
    typeof seven,
    seven
);

console.log(
    'PI:',
    typeof PI,
    PI
);

// Both of the above are type: number

console.log(
    'parseInt(4.95)',
    parseInt(4.95) // *doesn't round! Just cuts off decimal
);

console.log(
    'parseFloat("5.5")',
    typeof parseFloat("5.5"),
    parseFloat("5.5")
);

const myNewNum = Number('abc');

console.log(
    'myNewNum:',
    typeof myNewNum,
    myNewNum
);

